import React from "react";
import styled from "styled-components";

const AboutWrapper = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center
  align-items: center;
  padding: 3rem 7rem;
  background-color: ${({ theme }) => theme.secondaryBackground};
  color: ${({ theme }) => theme.text};
`;

const AboutContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 800px;
`;

const Title = styled.h2`
  font-size: 2rem;
  align-items: center;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  font-size: 1.2rem;
  text-align: center;
`;

const SkillsWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  margin-top: 2rem;
`;

const Skill = styled.div`
  background-color: ${({ theme }) => theme.primaryBackground};
  color: ${({ theme }) => theme.text};
  padding: 0.5rem 1rem;
  margin: 0.5rem;
  border-radius: 5px;
`;

const skills = ["HTML", "CSS", "JavaScript", "React", "Node.js", "MongoDB"];

const About = () => {
  return (
    <AboutWrapper>
      <AboutContent>
        <Title>About Me</Title>
        <Description>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Duis ac
          tellus at magna commodo tincidunt vel a leo. Proin malesuada sem in
          enim commodo, nec eleifend elit commodo. Integer accumsan vel enim ut
          consectetur. Nulla commodo eget mauris ac bibendum.
        </Description>
        <SkillsWrapper>
          {skills.map((skill, index) => (
            <Skill key={index}>{skill}</Skill>
          ))}
        </SkillsWrapper>
      </AboutContent>
    </AboutWrapper>
  );
};

export default About;
